import {useState} from 'react';

const Example = () => {
  const [countA, setCountA] = useState(0);
  return (
    <>
      <p>ボタンAを{countA}回押しました</p>
      <button onClick={() => {
        setCountA(countA + 1);
      }}>countAボタン</button>

      <p>ボタンBを{countB}回押しました</p>
      <button onClick={() => {
        setCountB(countB + 1);
      }}>countBボタン</button>

      <p>ボタンAを{countA}回押しました</p>
      <button onClick={() => {
        setCountA(countA + 1);
      }}>countAボタン</button>
    </>
  )
};

export default Example;
